Analytics.Script:
create temporary table APIThrottleData USING CarbonAnalytics OPTIONS(tableName "ORG_WSO2_APIMGT_STATISTICS_THROTTLE");
create temporary table APIRequestData USING CarbonAnalytics OPTIONS(tableName "ORG_WSO2_APIMGT_STATISTICS_REQUEST");
CREATE TEMPORARY TABLE THROTTLED_OUT_FINAL_SUMMARY USING CarbonAnalytics OPTIONS (tableName "API_THROTTLED_OUT_SUMMARY", schema "api string -i, api_version string -i, context string -i, apiPublisher string -i, applicationName string -i, tenantDomain string -i, year int -i, month int -i, day int -i, week int -i, time string -i, success_request_count int -i, throttleout_count int -i, max_request_time long -i, api_apiPublisher_applicationName_facet facet -i, applicationName_facet facet -i", primaryKeys "api,api_version,context,apiPublisher,applicationName,tenantDomain,year,month,day" );
insert into table THROTTLED_OUT_FINAL_SUMMARY select
IF(a.api IS NULL,b.api,a.api), IF(a.api_version IS NULL,b.api_version,a.api_version), IF(a.context IS NULL,b.context,a.context), IF(a.apiPublisher IS NULL,b.apiPublisher,a.apiPublisher), IF(a.applicationName IS NULL,b.applicationName,a.applicationName), IF(a.tenantDomain IS NULL,b.tenantDomain,a.tenantDomain), IF(a.year IS NULL,b.year,a.year), IF(a.month IS NULL,b.month,a.month), IF(a.day IS NULL,b.day,a.day), IF(a.week IS NULL,b.week,a.week), IF(a.time IS NULL,b.time,a.time), COALESCE(success_request_count,0), COALESCE(throttleout_count,0), COALESCE(a.times, COALESCE(b.times,0)), concat( IF(a.api IS NULL,b.api,a.api),concat(",",concat( IF(a.apiPublisher IS NULL,b.apiPublisher,a.apiPublisher),concat(",",IF(a.applicationName IS NULL,b.applicationName,a.applicationName)) ))), IF(a.applicationName IS NULL,b.applicationName,a.applicationName) from (select api,api_version,context,apiPublisher,applicationName,tenantDomain,first(requestTime) as times, substring(cast(first(requestTime)/1000 as timestamp),0,4) year, substring(cast(first(requestTime)/1000 as timestamp),6,2) month, substring(cast(first(requestTime)/1000 as timestamp),9,2) day, cast(first(requestTime)/(1000 * 60 * 60 *24 * 7) as INT) week, substring(cast(first(requestTime)/1000 as timestamp),0,16) time, sum(request) as success_request_count
from APIRequestData where context is not NULL group by api,api_version,context,apiPublisher,applicationName,tenantDomain, substring(cast(requestTime/1000 as timestamp),0,4) , substring(cast(requestTime/1000 as timestamp),6,2), substring(cast(requestTime/1000 as timestamp),9,2), cast(requestTime/(1000 * 60 * 60 *24 * 7) as INT) ) a full outer join
(select api,api_version,context,apiPublisher,applicationName,tenantDomain,first(throttledTime) as times, substring(cast(first(throttledTime)/1000 as timestamp),0,4) year, substring(cast(first(throttledTime)/1000 as timestamp),6,2) month, substring(cast(first(throttledTime)/1000 as timestamp),9,2) day, cast(first(throttledTime)/(1000 * 60 * 60 *24 * 7) as INT) week, substring(cast(first(throttledTime)/1000 as timestamp),0,16) time, count(apiPublisher) as throttleout_count from APIThrottleData where context is not NULL group by api,api_version,context,apiPublisher,applicationName,tenantDomain, substring(cast(throttledTime/1000 as timestamp),0,4), substring(cast(throttledTime/1000 as timestamp),6,2), substring(cast(throttledTime/1000 as timestamp),9,2), cast((throttledTime/(1000 * 60 * 60 *24 * 7)) as INT) ) b ON a.api=b.api AND a.api_version=b.api_version AND a.context=b.context AND a.apiPublisher=b.apiPublisher AND a.applicationName=b.applicationName AND a.tenantDomain=b.tenantDomain AND a.year=b.year AND a.month=b.month AND a.day=b.day AND a.week=b.week;
